import React, { useState, useEffect } from 'react';
import './App.css';

const bands = [
  {
    userId: 9,
    id: 1,
    title: 'Nightwish',
  },
  {
    userId: 10,
    id: 2,
    title: 'Metallica',
  },
  {
    userId: 3,
    id: 3,
    title: 'Nirvana',
  },
];

function App() {
  const [data, setData] = useState([]);
  const [sortType, setSortType] = useState('user_id');

  useEffect(() => {
    const sortArray = type => {
      const types = {
        userId: 'user_id',
        id: 'id',
        title: 'title',
      };
      const sortProperty = types[type];
      const sorted = [...bands].sort((a, b) => a[sortProperty] - b[sortProperty]);
      setData(sorted);
    };

    sortArray(sortType);
  }, [sortType]); 

  return (
    <div className="App">
      <select onChange={(e) => setSortType(e.target.value)}> 
        <option value="user_id">user_id</option>
        <option value="id">id</option>
        <option value="title">title</option>
      </select>

      {data.map(band => (
        <div key={band.id} style={{ margin: '30px' }}>
          <div>{`Band: ${band.userId}`}</div>
          <div>{`Albums: ${band.id}`}</div>
          <div>{`Members: ${band.title}`}</div>
        </div>
      ))}
    </div>
  );
}

export default App;
