import React, { useState, useEffect } from 'react';
import './App.css';

function Sorting(todos) {
  const [data, setData] = useState([]);
  const [sortType, setSortType] = useState('user_id');

  useEffect(() => {
    const sortArray = type => {
      const types = {
        userId: 'user_id',
        id: 'id',
        title: 'title',
      };
      const sortProperty = types[type];
      const sorted = [...todos].sort((a, b) => b[sortProperty] - a[sortProperty]);
      setData(sorted);
    };

    sortArray(sortType);
  }, [sortType]); 

  return (
    <div className="App">
      <select onChange={(e) => setSortType(e.target.value)}> 
        <option value="user_id">Albums</option>
        <option value="id">Members</option>
        <option value="title">Formed in</option>
      </select>
    </div>
  );
}

export default Sorting;
