import './App.css';
import React, { useEffect, useState} from 'react';
import Axios from 'axios';
import Todolist from './Todolist';

function App() {
  const [todos, setTodos] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [todosPerPage, setTodosPerPage] = useState(10);

  const fetchTodos = async () => {
    const { data } = await Axios.get(
      "https://jsonplaceholder.typicode.com/todos"
    );
    const todos = data;
    setTodos(todos);
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const deleteTodo = (todoId) => {
    setTodos(todos.filter((todo) => todo.id !== todoId))
  }

  const completedTodo = (todoId) => {
    setTodos(
      todos.map((todo) => 
        todo.id == todoId ? 
        {...todo, completed: !todo.completed}
        : todo))
  }

  const indexLastTodo = currentPage * todosPerPage;
  const indexFirstTodo = indexLastTodo - todosPerPage;
  const currentTodos = posts.slice(indexFirstTodo, indexLastTodo);

  return (
    <div className="App">
      <Todolist 
      todos={todos}
      onDelete={deleteTodo}
      onCompleted={completedTodo}
      />
    </div>
  );
}

export default App;
