import React, { useState, useEffect } from 'react';
import './App.css';
import Todolist from './Todolist'

const bands = [
  {
    id: 1,
    title: 'Nightwish',
    userId: 9,
    members: 6,
    formed_in: 1996,
  },
  {
    id: 2,
    title: 'Metallica',
    userId: 10,
    members: 4,
    formed_in: 1981,
  },
  {
    id: 3,
    title: 'Nirvana',
    userId: 3,
    members: 3,
    formed_in: 1987,
  },
];

function App() {
  const [data, setData] = useState([]);
  const [sortType, setSortType] = useState('userId');

  useEffect(() => {
    const sortArray = type => {
      const types = {
        userId: 'userId',
        members: 'members',
        formed: 'formed_in',
        id: 'id',
        title: 'title'
      };
      const sortProperty = types[type];
      const sorted = [...bands].sort(function(a,b){
        if(a.title.toLowerCase() < b.title.toLowerCase()) return -1;
        if(a.title.toLowerCase() > b.title.toLowerCase()) return 1;
        return 0;
      })
      .map((item, i) => <Todolist key={i} data={item} />);
      /*const sorted = [...bands].sort((a, b) => a[sortProperty] - b[sortProperty]);*/
      setData(sorted);
    };

    sortArray(sortType);
  }, [sortType]); 

  return (
    <div className="App">
      <select onChange={(e) => setSortType(e.target.value)}> 
        <option value="userId">UserID</option>
        <option value="members">Members</option>
        <option value="formed">Formed in</option>
        <option value="id">id</option>
        <option value="title">title</option>


      </select>

      {data.map(band => (
        <div key={band.id} style={{ margin: '30px' }}>
          <div>{`Band: ${band.id}`}</div>
          <div>{`UserId: ${band.userId}`}</div>
          <div>{`Members: ${band.members}`}</div>
          <div>{`Year of founding: ${band.formed_in}`}</div>
          <div>{`title: ${band.title}`}</div>

        </div>
      ))}
    </div>
  );
}

export default App;
